// Selector
const todoInput = document.querySelector(".todo-input");
const todoButton = document.querySelector(".todo-button");
const todoList = document.querySelector(".todo-list");
const filterOption = document.querySelector(".filter-todo");
// Event
document.addEventListener("DOMContentLoaded", getTodos);
todoButton.addEventListener("click", (e) => {
  e.preventDefault();

  //Todo DIV
  const todoDiv = document.createElement("div"); // ici tu créer l'element
  todoDiv.classList.add("todo"); // ici tu lui ajoutes une class
  const newTodo = document.createElement("li");
  newTodo.innerText = todoInput.value; // tu lui ajoutes du TEXT
  newTodo.classList.add("todo-item"); // ajoute la classe
  todoDiv.appendChild(newTodo); // tu ajoutes tes 'Li' après la div
  // ADD TODO TO LOCALSTORAGE
  saveLocalTodos(todoInput.value);
  //CHECK MARK BUTTON

  const completedButton = document.createElement("button");
  completedButton.innerHTML = '<i class="fas fa-check"></i>';
  completedButton.classList.add("complete-btn");
  todoDiv.appendChild(completedButton);

  //CHECK TRASH BUTTON

  const trashButton = document.createElement("button");
  trashButton.innerHTML = '<i class="fas fa-trash"></i>';
  trashButton.classList.add("trash-btn");
  todoDiv.appendChild(trashButton);
  // APPEND TO LIST

  todoList.appendChild(todoDiv);
  // Clear input

  todoInput.value = "";
});
todoList.addEventListener("click", (e) => {
  const item = e.target;
  // delete
  if (item.classList[0] === "trash-btn") {
    const todo = item.parentElement;
    //animation
    todo.classList.add("fall");
    removeLocalTodos(todo);
    todo.addEventListener("transitionend", () => {
      todo.remove(); // Fonction qui permet d'attendre la fin de la transition avant de supprimer.
    });
  }

  if (item.classList[0] === "complete-btn") {
    const todo = item.parentElement;
    todo.classList.toggle("completed");
  }
});
filterOption.addEventListener("click", (e) => {
  const todos = todoList.childNodes;
  todos.forEach((todo) => {
    switch (e.target.value) {
      case "all":
        todo.style.display = "flex";
        break;
      case "completed":
        if (todo.classList.contains("completed")) {
          todo.style.display = "flex";
        } else {
          todo.style.display = "none";
        }
        break;
      case "uncompleted":
        if (!todo.classList.contains("completed")) {
          todo.style.display = "flex";
        } else {
          todo.style.display = "none";
        }
        break;
    }
  });
});

function saveLocalTodos(todo) {
  let todos;
  if (localStorage.getItem("todos") === null) {
    todos = [];
  } else {
    todos = JSON.parse(localStorage.getItem("todos"));
  }
  todos.push(todo);
  localStorage.setItem("todos", JSON.stringify(todos));
}
